var getSlug = function ( str ) {
	var slug;

	// trim
	slug = str.trim();

	// get rid of non-alphanumerics
	slug = slug.replace( /[^0-9a-zA-Z]/g, '-' );

	// get rid of duplicate hyphens
	slug = slug.replace( /-+/g, '-' );

	// lowercase the whole thing
	slug = slug.toLowerCase();

	return slug;
};

module.exports = getSlug;