var byDate;

byDate = function ( a, b ) {
	if ( a.date.year !== b.date.year ) {
		return b.date.year - a.date.year;
	}

	if ( a.date.month !== b.date.month ) {
		return b.date.month - a.date.month;
	}

	if ( a.date.day !== b.date.day ) {
		return b.date.day - a.date.day;
	}

	return 0;
};

module.exports = byDate;