var getPageCollections, getPageCollection;

getPageCollection = require( 'getPageCollection' );


getPageCollections = function ( pages, data, partials ) {
	var result = [], i;

	if ( !pages || !pages.length ) {
		return result;
	}

	i = pages.length;
	while ( i-- ) {
		result[ result.length ] = getPageCollection( pages[i], data, partials );
	}

	return result;
};

module.exports = getPageCollections;