var getPartials, getPartial;

getPartial = require( 'getPartial' );


getPartials = function ( partials, data ) {
	var result = {}, i;

	if ( !partials || !partials.length ) {
		return result;
	}

	i = partials.length;
	while ( i-- ) {
		result[ partials[i].id ] = getPartial( partials[i], data );
	}

	return result;
};

module.exports = getPartials;