var getPostLocation, months, getPath;

months = require( 'months' );

getPath = function ( post ) {
	return post.date.year + '/' + months[ post.date.month ] + '/' + post.id + '.html';
};

getPostLocation = {
	url: function ( post ) {
		return '/blog/' + getPath( post );
	},

	fs: function ( post ) {
		return 'project/src/blog/' + getPath( post );
	}
};

module.exports = getPostLocation;