var readDate = function ( datestr ) {
	var date = {};

	if ( !datestr ) {
		return false;
	}

	date.year = +datestr.substr( 0, 4 );
	date.month = +datestr.substr( 5, 2 );
	date.day = +datestr.substr( 8, 2 );

	// sanity check - quick and dirty
	if ( date.year < 2013 ) { return false; }
	if ( date.month > 12 ) { return false; }
	if ( date.day > 31 ) { return false; }

	return date;
};

module.exports = readDate;